import React, { useState } from "react";
import Classes from "./Home.module.css";
import InstCard from "../../components/common/InstCard";
import Card from "../../components/common/card";
import ProgressBar from "./ProgressBar/ProgressBar";
import ImageGrid from "./ImageGrid/ImageGrid";

const cakes = {
  colOne: ["lavender-cake", "burnt-sugar-cake"],
  colTwo: ["garlic-knots", "pavlova"],
  colThree: ["bread-bowls", "apple-blueberry"],
};

const Home = () => {
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);

  const handleFile = (event) => {
    const types = ["image/jpg", "image/jpeg"];
    const file = event.target.files[0];
    if (file && types.includes(file.type)) {
      setFile(file);
      setError("");
    } else {
      setFile(null);
      setError("Please select an image file (png or jpg)");
    }
  };
  return (
    <div>
      <form className={Classes.Form}>
        <label className={Classes.Label}>
          <input type="file" onChange={handleFile} />
          <span>+</span>
        </label>
        <div className={Classes.Output}>
          {error && <div className={Classes.Error}>{error}</div>}
          {file && <div>{file.name}</div>}
          {file && <ProgressBar file={file} setfile={setFile} />}
        </div>
      </form>
      <div>
        <ImageGrid />
        {/* <div className={Classes.Column}>
          {cakes.colOne.map((imageLocation, id) => {
            return (
              <Card key={id} style={Classes.Card}>
                <div className={Classes.Square}></div>
                <InstCard imageLocation={imageLocation} />
              </Card>
            );
          })}
        </div>
        <div className={Classes.Column}>
          {cakes.colThree.map((imageLocation, id) => {
            return (
              <Card key={id} style={Classes.Card}>
                <div className={Classes.Square}></div>
                <InstCard imageLocation={imageLocation} />
              </Card>
            );
          })}
        </div>
        <div className={Classes.Column}>
          {cakes.colTwo.map((imageLocation, id) => {
            return (
              <Card key={id} style={Classes.Card}>
                <div className={Classes.Square}></div>
                <InstCard imageLocation={imageLocation} />
              </Card>
            );
          })}
        </div>*/}
      </div>
    </div>
  );
};

export default Home;
